"use strict";let form=document.querySelector(".form"),password=document.querySelector(".password"),submitbutton=document.querySelector(".form-button");var FORMTEST={minCharacters:8,specialsign:RegExp(/[{}.,!#:]/),numbers:RegExp(/([0-9])\w{1}/),capitals:RegExp(/[A-Z]/),noncapitals:RegExp(/[a-z]/)};function checkForm(e){if(console.log(e),!(e.length>FORMTEST.minCharacters&&FORMTEST.specialsign.test(e)&&FORMTEST.numbers.test(e)&&FORMTEST.capitals.test(e)&&FORMTEST.noncapitals.test(e)))return console.log("PASSWORD NOT ACCEPTED"),!1;console.log("CONGRATULATIONS U PASSED")}form.addEventListener("submit",function(e){e.preventDefault(),checkForm(password.value)});